(10, 1, '2019-07-05 19:53:19', '2019-07-05 17:53:19', '<!-- wp:image {"id":67,"align":"left","width":362,"height":362} -->\n<div class="wp-block-image"><figure class="alignleft is-resized"><img src="http://localhost:8080/wp-content/uploads/2019/07/polaroid_menu_shows_ziri.png" alt="" class="wp-image-67" width="362" height="362"/></figure></div>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Dorota Mucha</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>"Las Księżniczek"</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>hodowla psów rasy hovawart i lagotto romagnolo koło Świebodzina </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>hodowla@hovawart.pl&nbsp;</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>https://www.facebook.com/lasksiezniczek/ </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>tel. 660-261-252</p>\n<!-- /wp:paragraph -->', 'Kontakt', '', 'publish', 'closed', 'closed', '', 'kontakt', '', '', '2019-07-19 13:07:18', '2019-07-19 11:07:18', '', 0, 'http://localhost:8080/?page_id=10', 0, 'page', '', 0),
